export default function AppLite() {
  const [theme, setTheme] = React.useState('./squeeze2.mp4')
  const themeSource = React.useMemo(() => {
    return theme;
  }, [theme, localStorage.getItem('themedBG')])
  const [darkMode, toggleDarkMode] = useDarkModeManager()
  const value = localStorage.getItem("hasOverride");
  React.useEffect(() => {
    if (!value && !darkMode) {
      toggleDarkMode();
      localStorage.setItem("hasOverride", "1");
    }
  }, [value])
  const [style, setStyle] = useState({ background: '#333' })
  const Video = React.useMemo(() => {
    return (
      <VideoWrapper style={style} >
      </VideoWrapper>
    )
  }, [themeSource, theme, localStorage.getItem(THEME_BG_KEY)])
  const { chainId, account, library } = useWeb3React()

  const GainsPage = (props: any) => <TokenBalanceContextProvider><VotePage {...props} /></TokenBalanceContextProvider>
  return (
    <ErrorBoundary>
      <Route component={DarkModeQueryParamReader} />
      <Route component={ApeModeQueryParamReader} />
      <Web3ReactManager>
        <GelatoProvider
          library={library}
          chainId={chainId}
          account={account ?? undefined}
          useDefaultTheme={false}
          useDarkMode
        >
          <ApolloProvider client={(!chainId || chainId && chainId === 1) ? client : chainId && chainId === 56 ? bscClient : client}>
            <AppWrapper>
              {Video}
              <BodyWrapper>
                <Switch>

                  <Route exact strict path="/sendlite" component={RedirectPathToSwapOnly} />
                  <Route exact strict path="/swaplite/:outputCurrency" component={RedirectToSwap} />
                  <Route exact strict path="/swaplite" component={SwapLite} />

                  <Route exact strict path="/add/v2/:currencyIdA?/:currencyIdB?" component={RedirectDuplicateTokenIdsV2} />
                  <Route
                    exact
                    strict
                    path="/add/:currencyIdA?/:currencyIdB?/:feeAmount?"
                    component={RedirectDuplicateTokenIds}
                  />

                  <Route
                    exact
                    strict
                    path="/increase/:currencyIdA?/:currencyIdB?/:feeAmount?/:tokenId?"
                    component={AddLiquidity}
                  />

                  <Route exact strict path="/remove/v2/:currencyIdA/:currencyIdB" component={RemoveLiquidity} />
                  <Route exact strict path="/remove/:tokenId" component={RemoveLiquidityV3} />

                  <Route exact strict path="/migrate/v2" component={MigrateV2} />
                  <Route exact strict path="/migrate/v2/:address" component={MigrateV2Pair} />

                  <Route exact strict path="/proposals" component={CreateProposal} />
                  <Route exact strict path="/charts" component={ChartPage} />


                </Switch>

                <Marginer />


              </BodyWrapper>

            </AppWrapper>
          </ApolloProvider>
        </GelatoProvider>

      </Web3ReactManager>

    </ErrorBoundary>
  )
}